"use strict";window.addEventListener("DOMContentLoaded",()=>{burgerMenu(),slider()});const burgerMenu=()=>{const e=document.getElementById("menu");e.addEventListener("click",()=>{e.classList.toggle("change"),document.getElementById("nav").classList.toggle("change"),document.getElementById("menu-bg").classList.toggle("change-bg")})},slider=()=>{const e=document.querySelectorAll(".slider__item"),t=document.querySelector(".prev"),n=document.querySelector(".next"),c=document.querySelector(".slider__dots"),s=document.querySelectorAll(".dot");let d=1;const l=t=>{t>e.length&&(d=1),t<1&&(d=e.length),e.forEach(e=>e.style.display="none"),s.forEach(e=>e.classList.remove("dot-active")),e[d-1].style.display="flex",s[d-1].classList.add("dot-active")};l(d),t.addEventListener("click",()=>{l(d-=1)}),n.addEventListener("click",()=>{l(d+=1)}),c.addEventListener("click",({target:e})=>{s.forEach((t,n)=>{e.classList.contains("dot")&&e===t&&l(d=n+1)})})};